struct OCCWithdrawData {
    bytes32 accountId;
    bytes32 sender;
    bytes32 receiver;
    bytes32 brokerHash;
    uint64 tokenAmount;
    uint64 fee;
    uint64 chainId;
    uint64 withdrawNonce;
}

struct OCCDepositData {
    bytes32 accountId;
    bytes32 brokerHash;
    bytes32 solAddress;
    bytes32 tokenHash;
    uint64 chainId;
    uint64 tokenAmount;
    uint64 depositNonce;
}

contract SolCCMock is SolCC, ISolCC {
    uint64 public depositNonce;
    uint256 public constant SOL_CHAIN_ID = 902902902; // solana chain id for devnet
    uint256 public constant ORDERLY_CHAIN_ID = 4460;
    uint256 public constant ARBITRUM_CHAIN_ID = 421614;
