        AccountWithdrawSol memory withdrawData = AccountWithdrawSol(
            Utils.getSolAccountId(_withdrawData.sender, _withdrawData.brokerId),
            _withdrawData.sender,
            _withdrawData.receiver,
            Utils.calculateStringHash(_withdrawData.brokerId),
            Utils.calculateStringHash(_withdrawData.tokenSymbol),
            _withdrawData.tokenAmount,
            _withdrawData.fee,
            _withdrawData.chainId,
            _withdrawData.withdrawNonce
        );

        bytes memory payload = abi.encodePacked(
            withdrawData.accountId,
            withdrawData.sender,
            withdrawData.receiver,
            Utils.calculateStringHash(_withdrawData.brokerId),
            Utils.calculateStringHash(_withdrawData.tokenSymbol),
            uint64(withdrawData.tokenAmount),
            uint64(withdrawData.fee),
            uint64(withdrawData.chainId),
            uint64(withdrawData.withdrawNonce)
        );
        uint32 dstEid = SOL_EID;
        bytes memory options = OptionsBuilder.newOptions().addExecutorLzReceiveOption(500000, 0);
        MessagingFee memory _msgFee = _quote(dstEid, payload, options, false);
        emit MessageReceived(_msgFee.nativeFee, _msgFee.lzTokenFee, payload);
        MessagingReceipt memory msgReceipt = _lzSend(dstEid, payload, options, _msgFee, address(this));
    }

    function deposit(
        bytes32 _solAddress,
        string calldata brokerId,
        string calldata token,
        uint128 tokenAmount
    ) external {
        AccountDepositSol memory depositData = AccountDepositSol(
            Utils.getSolAccountId(_solAddress, brokerId),
            Utils.calculateStringHash(brokerId),
            _solAddress,
            Utils.calculateStringHash(token),
            SOL_CHAIN_ID,
            tokenAmount,
            _newDepositNonce()
